<?php/** Defino o local onde esta a classe */namespace vendor\model;class Configurations{    /** Declaro as variavéis da classe */    private $connection = null;    private $sql = null;    private $stmt = null;    private $configurationId = null;    private $title = null;    private $description = null;    private $keywords = null;    private $author = null;    private $copyright = null;    /** Construtor da classe */    function __construct()    {        /** Cria o objeto de conexão com o banco de dados */        $this->connection = new MySql();    }    /** Salvo ou atualizo um registro */    public function Save(int $situationId, string $name, string $description, string $history): bool    {        /** Parâmetros de Entrada */        $this->situationId = $situationId;        $this->name = $name;        $this->description = $description;        $this->history = $history;        /** Verifico o tipo de operação */        if ($situationId === 0)        {            /** Consulta SQL */            $this->sql = 'INSERT INTO SITUATIONS(situation_id,                                                  name,                                                 description,                                                 history)                                       VALUES(:situationId,                                               :name,                                              :description,                                              :history);';        }        else        {            /** Consulta SQL */            $this->sql = 'UPDATE SITUATIONS SET                           name = :name,                           description = :description,                           history = :history                          WHERE situation_id = :situationId';        }        /** Preparo o SQL para execução */        $this->stmt = $this->connection->connect()->prepare($this->sql);        /** Preenchimento dos parâmetros */        $this->stmt->bindParam(':situationId', $this->situationId);        $this->stmt->bindParam(':name', $this->name);        $this->stmt->bindParam(':description', $this->description);        $this->stmt->bindParam(':history', $this->history);        /** Executo o SQL */        return $this->stmt->execute();    }    /** Consulta de um registro especifico */    public function Get(int $configurationId)    {        /** Parâmetros de Entrada */        $this->configurationId = $configurationId;        /** Consulta SQL */        $this->sql = 'SELECT * FROM CONFIGURATIONS WHERE configuration_id = :configurationId;';        /** Preparo o SQL para execução */        $this->stmt = $this->connection->connect()->prepare($this->sql);        /** Preenchimento dos parâmetros */        $this->stmt->bindParam(':configurationId', $this->configurationId);        /** Executo o SQL */        $this->stmt->execute();        /** Retorno o resultado */        return $this->stmt->fetchObject();    }    /** Remoção de um registro especifico */    public function Delete(int $configurationId): bool    {        /** Parâmetros de Entrada */        $this->configurationId = $configurationId;        /** Consulta SQL */        $this->sql = 'DELETE FROM CONFIGURATIONS WHERE configuration_id = :configurationId;';        /** Preparo o SQL para execução */        $this->stmt = $this->connection->connect()->prepare($this->sql);        /** Preenchimento dos parâmetros */        $this->stmt->bindParam(':configurationId', $this->configurationId);        /** Executo o SQL */        return $this->stmt->execute();    }    /** Listagem de todos os registros */    public function All()    {        /** Consulta SQL */        $this->sql = 'SELECT * FROM CONFIGURATIONS ORDER BY CONFIGURATION_ID DESC LIMIT 1;';        /** Preparo o SQL para execução */        $this->stmt = $this->connection->connect()->prepare($this->sql);        /** Executo o SQL */        $this->stmt->execute();        /** Retorno o resultado */        return $this->stmt->fetchObject();    }    /** Fecha uma conexão aberta anteriormente com o banco de dados */    function __destruct()    {        $this->connection = null;    }}