<?php/** Defino o local onde esta a classe */namespace vendor\model;class ContentCategories{    /** Declaro as variavéis da classe */    private $connection = null;    private $sql = null;    private $stmt = null;    private $contentCategoryId = null;    private $situationId = null;    private $userId = null;    private $name = null;    private $description = null;    private $history = null;    /** Construtor da classe */    function __construct()    {        /** Cria o objeto de conexão com o banco de dados */        $this->connection = new MySql();    }    /** Salvo ou atualizo um registro */    public function Save(int $contentCategoryId, int $situationId, int $userId, string $name, string $description, string $history): bool    {        /** Parâmetros de Entrada */        $this->contentCategoryId = $contentCategoryId;        $this->situationId = $situationId;        $this->userId = $userId;        $this->name = $name;        $this->description = $description;        $this->history = $history;        /** Verifico o tipo de operação */        if ($this->contentCategoryId === 0)        {            /** Consulta SQL */            $this->sql = 'INSERT INTO content_categories(content_category_id,                                                         situation_id,                                                         user_id,                                                         name,                                                         description,                                                         history)                                       VALUES(:contentCategoryId,                                              :situationId,                                              :userId,                                              :name,                                              :description,                                              :history);';        }        else        {            /** Consulta SQL */            $this->sql = 'UPDATE content_categories SET                           situation_id = :situationId,                          user_id = :userId,                          name = :name,                          description = :description,                          history = :history                          WHERE content_category_id = :contentCategoryId';        }        /** Preparo o SQL para execução */        $this->stmt = $this->connection->connect()->prepare($this->sql);        /** Preenchimento dos parâmetros */        $this->stmt->bindParam(':contentCategoryId', $this->contentCategoryId);        $this->stmt->bindParam(':situationId', $this->situationId);        $this->stmt->bindParam(':userId', $this->userId);        $this->stmt->bindParam(':name', $this->name);        $this->stmt->bindParam(':description', $this->description);        $this->stmt->bindParam(':history', $this->history);        /** Executo o SQL */        return $this->stmt->execute();    }    /** Consulta de um registro especifico */    public function Get(int $contentCategoryId)    {        /** Parâmetros de Entrada */        $this->contentCategoryId = $contentCategoryId;        /** Consulta SQL */        $this->sql = 'SELECT * FROM content_categories WHERE content_category_id = :contentCategoryId;';        /** Preparo o SQL para execução */        $this->stmt = $this->connection->connect()->prepare($this->sql);        /** Preenchimento dos parâmetros */        $this->stmt->bindParam(':contentCategoryId', $this->contentCategoryId);        /** Executo o SQL */        $this->stmt->execute();        /** Retorno o resultado */        return $this->stmt->fetchObject();    }    /** Remoção de um registro especifico */    public function Delete(int $contentCategoryId): bool    {        /** Parâmetros de Entrada */        $this->contentCategoryId = $contentCategoryId;        /** Consulta SQL */        $this->sql = 'DELETE FROM content_categories WHERE content_category_id = :contentCategoryId;';        /** Preparo o SQL para execução */        $this->stmt = $this->connection->connect()->prepare($this->sql);        /** Preenchimento dos parâmetros */        $this->stmt->bindParam(':contentCategoryId', $this->contentCategoryId);        /** Executo o SQL */        return $this->stmt->execute();    }    /** Listagem de todos os registros */    public function All()    {        /** Consulta SQL */        $this->sql = 'SELECT * FROM content_categories;';        /** Preparo o SQL para execução */        $this->stmt = $this->connection->connect()->prepare($this->sql);        /** Executo o SQL */        $this->stmt->execute();        /** Retorno o resultado */        return $this->stmt->fetchAll(\PDO::FETCH_OBJ);    }    /** Fecha uma conexão aberta anteriormente com o banco de dados */    function __destruct()    {        $this->connection = null;    }}