<?php/** Defino o local onde esta a classe */namespace vendor\model;class ContentsSubs{    /** Declaro as variavéis da classe */    private $connection = null;    private $sql = null;    private $stmt = null;    private $contentSubId = null;    private $contentId = null;    private $highlighterId = null;    private $situationId = null;    private $userId = null;    private $positionContent = null;    private $positionMenu = null;    private $url = null;    private $title = null;    private $titleMenu = null;    private $description = null;    private $contentResume = null;    private $contentComplete = null;    private $keywords = null;    private $dateStart = null;    private $dateClosing = null;    private $history = null;    /** Construtor da classe */    function __construct()    {        /** Cria o objeto de conexão com o banco de dados */        $this->connection = new MySql();    }    /** Salvo ou atualizo um registro */    public function Save(int $contentSubId, int $contentId, int $highlighterId, int $situationId, int $userId, int $positionContent, int $positionMenu, string $url, string $title, string $titleMenu, string $description, string $contentResume, string $contentComplete, string $keywords, string $dateStart, string $dateClosing, string $history): bool    {        /** Parâmetros de Entrada */        $this->contentSubId = $contentSubId;        $this->contentId = $contentId;        $this->highlighterId = $highlighterId;        $this->situationId = $situationId;        $this->userId = $userId;        $this->positionContent = $positionContent;        $this->positionMenu = $positionMenu;        $this->url = $url;        $this->title = $title;        $this->titleMenu = $titleMenu;        $this->description = $description;        $this->contentResume = $contentResume;        $this->contentComplete = $contentComplete;        $this->keywords = $keywords;        $this->dateStart = $dateStart;        $this->dateClosing = $dateClosing;        $this->history = $history;        /** Verifico o tipo de operação */        if ($this->contentSubId === 0)        {            /** Consulta SQL */            $this->sql = 'INSERT INTO contents_subs(content_sub_id,                                                    content_id,                                                    highlighter_id,                                                    situation_id,                                                    user_id,                                                    position_content,                                                    position_menu,                                                    url,                                                    title,                                                    title_menu,                                                    description,                                                    content_resume,                                                    content_complete,                                                    keywords,                                                    date_start,                                                    date_closing,                                                    history)                                       VALUES(:contentSubId,                                              :contentId,                                              :highlighterId,                                              :situationId,                                              :userId,                                              :positionContent,                                              :positionMenu,                                              :url,                                              :title,                                              :titleMenu,                                              :description,                                              :contentResume,                                              :contentComplete,                                              :keywords,                                              :dateStart,                                              :dateClosing,                                              :history);';        }        else        {            /** Consulta SQL */            $this->sql = 'UPDATE contents_subs SET                           content_id = :contentId,                          highlighter_id = :highlighterId,                          situation_id = :situationId,                          user_id = :userId,                          position_content = :positionContent,                          position_menu = :positionMenu,                          url = :url,                          title = :title,                          title_menu = :titleMenu,                          description = :description,                          content_resume = :contentResume,                          content_complete = :contentComplete,                          keywords = :keywords,                          date_start = :dateStart,                          date_closing = :dateClosing,                          history = :history                          WHERE content_sub_id = :contentSubId';        }        /** Preparo o SQL para execução */        $this->stmt = $this->connection->connect()->prepare($this->sql);        /** Preenchimento dos parâmetros */        $this->stmt->bindParam(':contentSubId', $this->contentSubId);        $this->stmt->bindParam(':contentId', $this->contentId);        $this->stmt->bindParam(':highlighterId', $this->highlighterId);        $this->stmt->bindParam(':situationId', $this->situationId);        $this->stmt->bindParam(':userId', $this->userId);        $this->stmt->bindParam(':positionContent', $this->positionContent);        $this->stmt->bindParam(':positionMenu', $this->positionMenu);        $this->stmt->bindParam(':url', $this->url);        $this->stmt->bindParam(':title', $this->title);        $this->stmt->bindParam(':titleMenu', $this->titleMenu);        $this->stmt->bindParam(':description', $this->description);        $this->stmt->bindParam(':contentResume', $this->contentResume);        $this->stmt->bindParam(':contentComplete', $this->contentComplete);        $this->stmt->bindParam(':keywords', $this->keywords);        $this->stmt->bindParam(':dateStart', $this->dateStart);        $this->stmt->bindParam(':dateClosing', $this->dateClosing);        $this->stmt->bindParam(':history', $this->history);        /** Executo o SQL */        return $this->stmt->execute();    }    /** Consulta de um registro especifico */    public function Get(int $contentSubId)    {        /** Parâmetros de Entrada */        $this->contentSubId = $contentSubId;        /** Consulta SQL */        $this->sql = 'SELECT * FROM contents_subs WHERE content_sub_id = :contentSubId;';        /** Preparo o SQL para execução */        $this->stmt = $this->connection->connect()->prepare($this->sql);        /** Preenchimento dos parâmetros */        $this->stmt->bindParam(':contentSubId', $this->contentSubId);        /** Executo o SQL */        $this->stmt->execute();        /** Retorno o resultado */        return $this->stmt->fetchObject();    }    /** Remoção de um registro especifico */    public function Delete(int $contentSubId): bool    {        /** Parâmetros de Entrada */        $this->contentSubId = $contentSubId;        /** Consulta SQL */        $this->sql = 'DELETE FROM contents_subs WHERE content_sub_id = :contentSubId;';        /** Preparo o SQL para execução */        $this->stmt = $this->connection->connect()->prepare($this->sql);        /** Preenchimento dos parâmetros */        $this->stmt->bindParam(':contentSubId', $this->contentSubId);        /** Executo o SQL */        return $this->stmt->execute();    }    /** Listagem de todos os registros */    public function All(int $contentId)    {        /** Parâmetros de Entrada */        $this->contentId = $contentId;        /** Consulta SQL */        $this->sql = 'SELECT * FROM contents_subs WHERE content_id = :contentId;';        /** Preparo o SQL para execução */        $this->stmt = $this->connection->connect()->prepare($this->sql);        /** Preenchimento dos parâmetros */        $this->stmt->bindParam(':contentId', $this->contentId);        /** Executo o SQL */        $this->stmt->execute();        /** Retorno o resultado */        return $this->stmt->fetchAll(\PDO::FETCH_OBJ);    }    /** Fecha uma conexão aberta anteriormente com o banco de dados */    function __destruct()    {        $this->connection = null;    }}