<?php/** Defino o local onde esta a classe */namespace vendor\model;class ContentsSubsFiles{    /** Declaro as variavéis da classe */    private $connection = null;    private $sql = null;    private $stmt = null;    private $contentSubFileId = null;    private $contentSubId = null;    private $highlighterId = null;    private $situationId = null;    private $userId = null;    private $positionContent = null;    private $name = null;    private $path = null;    /** Construtor da classe */    function __construct()    {        /** Cria o objeto de conexão com o banco de dados */        $this->connection = new MySql();    }    /** Salvo ou atualizo um registro */    public function Save(int $contentSubFileId, int $contentSubId, int $highlighterId, int $situationId, int $userId, int $positionContent, string $name, string $path): bool    {        /** Parâmetros de Entrada */        $this->contentSubFileId = $contentSubFileId;        $this->contentSubId = $contentSubId;        $this->highlighterId = $highlighterId;        $this->situationId = $situationId;        $this->userId = $userId;        $this->positionContent = $positionContent;        $this->name = $name;        $this->path = $path;        /** Verifico o tipo de operação */        if ($this->contentSubFileId === 0)        {            /** Consulta SQL */            $this->sql = 'INSERT INTO contents_subs_files(content_sub_file_id,                                                     content_sub_id,                                                     highlighter_id,                                                     situation_id,                                                     user_id,                                                     position_content,                                                     name,                                                     path)                                       VALUES(:contentSubFileId,                                              :contentSubId,                                              :highlighterId,                                              :situationId,                                              :userId,                                              :positionContent,                                              :name,                                              :path);';        }        else        {            /** Consulta SQL */            $this->sql = 'UPDATE contents_files SET                           content_sub_id = :contentSubId,                          highlighter_id = :highlighterId,                          situation_id = :situationId,                          user_id = :userId,                          position_content = :positionContent,                          name = :name,                          path = :path,                          WHERE content_sub_file_id = :contentSubFileId';        }        /** Preparo o SQL para execução */        $this->stmt = $this->connection->connect()->prepare($this->sql);        /** Preenchimento dos parâmetros */        $this->stmt->bindParam(':contentSubFileId', $this->contentSubFileId);        $this->stmt->bindParam(':contentSubId', $this->contentSubId);        $this->stmt->bindParam(':highlighterId', $this->highlighterId);        $this->stmt->bindParam(':situationId', $this->situationId);        $this->stmt->bindParam(':userId', $this->userId);        $this->stmt->bindParam(':positionContent', $this->positionContent);        $this->stmt->bindParam(':name', $this->name);        $this->stmt->bindParam(':path', $this->path);        /** Executo o SQL */        return $this->stmt->execute();    }    /** Consulta de um registro especifico */    public function Get(int $contentSubFileId)    {        /** Parâmetros de Entrada */        $this->contentSubFileId = $contentSubFileId;        /** Consulta SQL */        $this->sql = 'SELECT * FROM contents_subs_files WHERE content_sub_file_id = :contentSubFileId;';        /** Preparo o SQL para execução */        $this->stmt = $this->connection->connect()->prepare($this->sql);        /** Preenchimento dos parâmetros */        $this->stmt->bindParam(':contentSubFileId', $this->contentSubFileId);        /** Executo o SQL */        $this->stmt->execute();        /** Retorno o resultado */        return $this->stmt->fetchObject();    }    /** Remoção de um registro especifico */    public function Delete(int $contentSubFileId): bool    {        /** Parâmetros de Entrada */        $this->contentSubFileId = $contentSubFileId;        /** Consulta SQL */        $this->sql = 'DELETE FROM contents_subs_files WHERE content_sub_file_id = :contentSubFileId;';        /** Preparo o SQL para execução */        $this->stmt = $this->connection->connect()->prepare($this->sql);        /** Preenchimento dos parâmetros */        $this->stmt->bindParam(':contentSubFileId', $this->contentSubFileId);        /** Executo o SQL */        return $this->stmt->execute();    }    /** Listagem de todos os registros */    public function All(int $contentSubId)    {        /** Parâmetros de Entrada */        $this->contentSubId = $contentSubId;        /** Consulta SQL */        $this->sql = 'SELECT                      cf.content_sub_file_id,                      cf.content_sub_id,                      cf.path,                      cf.name,                      h.description                      FROM contents_subs_files cf                      JOIN highlighters h ON cf.highlighter_id = h.highlighter_id                        WHERE content_sub_id = :contentSubId';        /** Preparo o SQL para execução */        $this->stmt = $this->connection->connect()->prepare($this->sql);        /** Preenchimento dos parâmetros */        $this->stmt->bindParam(':contentSubId', $this->contentSubId);        /** Executo o SQL */        $this->stmt->execute();        /** Retorno o resultado */        return $this->stmt->fetchAll(\PDO::FETCH_OBJ);    }    /** Fecha uma conexão aberta anteriormente com o banco de dados */    function __destruct()    {        $this->connection = null;    }}