<?php/** Defino o local onde esta a classe */namespace vendor\model;class Highlighters{    /** Declaro as variavéis da classe */    private $connection = null;    private $sql = null;    private $stmt = null;    private $highlighterId = null;    private $name = null;    private $description = null;    private $history = null;    /** Construtor da classe */    function __construct()    {        /** Cria o objeto de conexão com o banco de dados */        $this->connection = new MySql();    }    /** Salvo ou atualizo um registro */    public function Save(int $highlighterId, string $name, string $description, string $history): bool    {        /** Parâmetros de Entrada */        $this->highlighterId = $highlighterId;        $this->name = $name;        $this->description = $description;        $this->history = $history;        /** Verifico o tipo de operação */        if ($this->highlighterId === 0)        {            /** Consulta SQL */            $this->sql = 'INSERT INTO HIGHLIGHTERS(highlighter_id,                                                  name,                                                 description,                                                 history)                                       VALUES(:highlighterId,                                               :name,                                              :description,                                              :history);';        }        else        {            /** Consulta SQL */            $this->sql = 'UPDATE HIGHLIGHTERS SET                           name = :name,                           description = :description,                           history = :history                          WHERE highlighter_id = :highlighterId';        }        /** Preparo o SQL para execução */        $this->stmt = $this->connection->connect()->prepare($this->sql);        /** Preenchimento dos parâmetros */        $this->stmt->bindParam(':highlighterId', $this->highlighterId);        $this->stmt->bindParam(':name', $this->name);        $this->stmt->bindParam(':description', $this->description);        $this->stmt->bindParam(':history', $this->history);        /** Executo o SQL */        return $this->stmt->execute();    }    /** Consulta de um registro especifico */    public function Get(int $highlighterId)    {        /** Parâmetros de Entrada */        $this->highlighterId = $highlighterId;        /** Consulta SQL */        $this->sql = 'SELECT * FROM HIGHLIGHTERS WHERE highlighter_id = :highlighterId;';        /** Preparo o SQL para execução */        $this->stmt = $this->connection->connect()->prepare($this->sql);        /** Preenchimento dos parâmetros */        $this->stmt->bindParam(':highlighterId', $this->highlighterId);        /** Executo o SQL */        $this->stmt->execute();        /** Retorno o resultado */        return $this->stmt->fetchObject();    }    /** Remoção de um registro especifico */    public function Delete(int $highlighterId): bool    {        /** Parâmetros de Entrada */        $this->highlighterId = $highlighterId;        /** Consulta SQL */        $this->sql = 'DELETE FROM HIGHLIGHTERS WHERE highlighter_id = :highlighterId;';        /** Preparo o SQL para execução */        $this->stmt = $this->connection->connect()->prepare($this->sql);        /** Preenchimento dos parâmetros */        $this->stmt->bindParam(':highlighterId', $this->highlighterId);        /** Executo o SQL */        return $this->stmt->execute();    }    /** Listagem de todos os registros */    public function All()    {        /** Consulta SQL */        $this->sql = 'SELECT * FROM HIGHLIGHTERS;';        /** Preparo o SQL para execução */        $this->stmt = $this->connection->connect()->prepare($this->sql);        /** Executo o SQL */        $this->stmt->execute();        /** Retorno o resultado */        return $this->stmt->fetchAll(\PDO::FETCH_OBJ);    }    /** Fecha uma conexão aberta anteriormente com o banco de dados */    function __destruct()    {        $this->connection = null;    }}