<?php/** Defino o local onde esta a classe */namespace vendor\model;class Situations{    /** Declaro as variavéis da classe */    private $connection = null;    private $sql = null;    private $stmt = null;    private $situationId = null;    private $name = null;    private $description = null;    private $history = null;    /** Construtor da classe */    function __construct()    {        /** Cria o objeto de conexão com o banco de dados */        $this->connection = new MySql();    }    /** Salvo ou atualizo um registro */    public function Save(int $situationId, string $name, string $description, string $history): bool    {        /** Parâmetros de Entrada */        $this->situationId = $situationId;        $this->name = $name;        $this->description = $description;        $this->history = $history;        /** Verifico o tipo de operação */        if ($this->situationId === 0)        {            /** Consulta SQL */            $this->sql = 'INSERT INTO situations(situation_id,                                                  name,                                                 description,                                                 history)                                       VALUES(:situationId,                                               :name,                                              :description,                                              :history);';        }        else        {            /** Consulta SQL */            $this->sql = 'UPDATE situations SET                           name = :name,                           description = :description,                           history = :history                          WHERE situation_id = :situationId';        }        /** Preparo o SQL para execução */        $this->stmt = $this->connection->connect()->prepare($this->sql);        /** Preenchimento dos parâmetros */        $this->stmt->bindParam(':situationId', $this->situationId);        $this->stmt->bindParam(':name', $this->name);        $this->stmt->bindParam(':description', $this->description);        $this->stmt->bindParam(':history', $this->history);        /** Executo o SQL */        return $this->stmt->execute();    }    /** Consulta de um registro especifico */    public function Get(int $situationId)    {        /** Parâmetros de Entrada */        $this->situationId = $situationId;        /** Consulta SQL */        $this->sql = 'SELECT * FROM situations WHERE situation_id = :situationId;';        /** Preparo o SQL para execução */        $this->stmt = $this->connection->connect()->prepare($this->sql);        /** Preenchimento dos parâmetros */        $this->stmt->bindParam(':situationId', $this->situationId);        /** Executo o SQL */        $this->stmt->execute();        /** Retorno o resultado */        return $this->stmt->fetchObject();    }    /** Remoção de um registro especifico */    public function Delete(int $situationId): bool    {        /** Parâmetros de Entrada */        $this->situationId = $situationId;        /** Consulta SQL */        $this->sql = 'DELETE FROM situations WHERE situation_id = :situationId;';        /** Preparo o SQL para execução */        $this->stmt = $this->connection->connect()->prepare($this->sql);        /** Preenchimento dos parâmetros */        $this->stmt->bindParam(':situationId', $this->situationId);        /** Executo o SQL */        return $this->stmt->execute();    }    /** Listagem de todos os registros */    public function All()    {        /** Consulta SQL */        $this->sql = 'SELECT * FROM situations;';        /** Preparo o SQL para execução */        $this->stmt = $this->connection->connect()->prepare($this->sql);        /** Executo o SQL */        $this->stmt->execute();        /** Retorno o resultado */        return $this->stmt->fetchAll(\PDO::FETCH_OBJ);    }    /** Fecha uma conexão aberta anteriormente com o banco de dados */    function __destruct()    {        $this->connection = null;    }}